package basics.namingconvetions;

/**
 * 
 */
public class NamingConventions {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Naming Conventions : ");
		
		System.out.println("Project Naming Conventions:");
		System.out.println("1. Starts with capital letter");
		System.out.println("2. sub sequent word starts with capital letter");
		System.out.println("3. Project name specify what is the purpose of the project");
		
		System.out.println("Package Naming Conventions:");
		System.out.println("1. starts with small letter");
		System.out.println("2. subseuent words also small letter");
		System.out.println("3. sub folder seperated by .(Dot)");
		
		System.out.println("Class Naming conventions:");
		System.out.println("1. Starts with capital letter");
		System.out.println("2. sub sequent word starts with capital letter");
		System.out.println("3. Class name specify what is the purpose of the Class");
		System.out.println("4. Class name length in between 8 to 25 Charactors");
		System.out.println("5. Class name never start with Number");
		System.out.println("6. As much as avoid numbers in Class names (not recomended)");
		
		System.out.println("Method Naming Convention:");
		System.out.println("1. Starts with small letter");
		System.out.println("2. sub sequent word starts with capital letter");
		System.out.println("3. Method name specify what is the purpose of the Method");
		System.out.println("4. Method name length in between 8 to 25 Charactors");		
		System.out.println("5. Method name never start with Number");
		System.out.println("6. As much as avoid numbers in Method names (not recomended)");
		
		System.out.println("Variable Naming Convention:");
		System.out.println("1. Starts with small letter");
		System.out.println("2. sub sequent word starts with capital letter");
		System.out.println("3. Variable name specify what is the purpose of the Variable");
		System.out.println("4. Variable name length in between 5 to 24 Charactors");		
		System.out.println("5. Variable name never start with Number (Not allowed)");
		System.out.println("6. As much as avoid numbers in Variable names (not recomended)");
		

	}

}
