package basics.operators.athameticoperators;

/**
 * 
 */
public class ArthameticOperatorExample {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int arthameticVariable = 22;
		int operatorValue = 7;
		int variable;
		arthameticVariable = arthameticVariable + arthameticVariable; 
		System.out.println("arthameticVariable value in addtion : " + arthameticVariable);

		variable = arthameticVariable - operatorValue; 
		System.out.println("arthameticVariable value in subtraction : " + variable);

		variable = arthameticVariable % operatorValue; 
		System.out.println("arthameticVariable value in modulo : " + variable);

		variable = arthameticVariable / operatorValue; 
		System.out.println("arthameticVariable value in division : " + variable);

		arthameticVariable = arthameticVariable * arthameticVariable; 
		System.out.println("arthameticVariable value in multification : " + arthameticVariable);
	}

	
	/*
	 * 3)20( 6 18 ---- 2
	 */
	
	
	
}
